#include <iostream>#include <string>#include "esconu.h"using namespace std;const int MAX = 5000;int main(){    BorraPantalla();    string nombre[MAX];    int edad[MAX];    float edadPromedio;    int x, registros;    cout << "Este programa calcula la edad promedio de X cantidad de  personas";    cout << endl << endl;    cout << "Digite cuantos registros entrarÃ¡: ";    cin >> registros;    for (x = 1; x <= registros; x++)    {        cout << "Digite el nombre del estudiante " << x << ": ";        cin >> nombre[x];        cout << "Digite la edad de " << nombre[x] << ": ";        cin >> edad[x];    }    edadPromedio = 0;    for (x = 1; x <= registros; x++)        edadPromedio += edad[x];    edadPromedio = edadPromedio / registros;    cout << "La edad promedio de los estudiantes es: " << edadPromedio << endl;    return 0;}