#include <iostream>#include <string>#include "esconu.h"using namespace std;int main(){    BorraPantalla();        string nombre1, nombre2, nombre3, nombre4, nombre5;    int edad1, edad2, edad3, edad4, edad5;    float edadPromedio;        cout << "Este programa calcula la edad promedio de 5 personas" << endl;    cout << endl;        cout << "Digite el nombre del estudiante 1: ";    cin >> nombre1;    cout << "Digite la edad de " << nombre1 << ": ";    cin >> edad1;        cout << "Digite el nombre del estudiante 2: ";    cin >> nombre2;    cout << "Digite la edad de " << nombre2 << ": ";    cin >> edad2;        cout << "Digite el nombre del estudiante 3: ";    cin >> nombre3;    cout << "Digite la edad de " << nombre3 << ": ";    cin >> edad3;        cout << "Digite el nombre del estudiante 4: ";    cin >> nombre4;    cout << "Digite la edad de " << nombre4 << ": ";    cin >> edad4;        cout << "Digite el nombre del estudiante 5: ";    cin >> nombre5;    cout << "Digite la edad de " << nombre5 << ": ";    cin >> edad5;        edadPromedio = (edad1 + edad2 + edad3 + edad4 + edad5) / 5;        cout << "La edad promedio de los estudiantes es: " << edadPromedio << endl;        return 0;}